SELECT MIN(role_type.role), MIN(cast_info.person_id), MIN(name.gender), MIN(aka_name.person_id), MIN(name.name), MIN(name.id), MIN(movie_keyword.movie_id), MIN(title.id), MIN(movie_info.note), MIN(complete_cast.movie_id), MIN(movie_info.movie_id) FROM name, movie_info, aka_name, cast_info, title, movie_keyword, role_type, complete_cast WHERE movie_info.note = '(featured on Unforgiven DVD)' AND name.name IN ('Smith, Mike', 'Johnson, Michael', 'Morgan, Alexandra', 'Zabaleta, Guillermo', 'Williams, John', 'Smith, Michael', 'Alié, René', 'Smith, Chris', 'Smith, David', 'Jones, David', 'Cowen, Howie', 'Bonnard, Gilles', 'Stalzer, Chris', 'Reyna, Antonio', 'Williams, David', 'Henry, Cory', 'Williams, Michael', 'Liégeois, Jean-Louis', 'Smith, Steve', 'Bosman, Dick') AND name.gender IS NULL AND role_type.role IN ('costume designer', 'production designer', 'editor', 'writer', 'cinematographer', 'miscellaneous crew', 'guest', 'actor', 'composer', 'director', 'producer', 'actress') AND movie_info.movie_id = complete_cast.movie_id AND movie_info.movie_id = title.id AND name.id = aka_name.person_id AND name.id = cast_info.person_id AND movie_keyword.movie_id = movie_info.movie_id;