SELECT MIN(movie_info_idx.info), MIN(cast_info.person_id), MIN(name.gender), MIN(name.name), MIN(name.id), MIN(movie_info_idx.movie_id), MIN(info_type.info), MIN(cast_info.movie_id) FROM name, info_type, cast_info, movie_info_idx WHERE name.gender = 'f' AND name.name != 'Stalzer, Chris' AND movie_info_idx.info != '5' AND info_type.info IN ('LD original title', 'where now', 'production dates', 'trivia', 'runtimes', 'votes distribution', 'LD audio noise', 'certificates', 'LD sharpness', 'LD contrast', 'opening weekend', 'LD video quality', 'color info', 'LD quality program', 'pictorial', 'LD laserdisc title', 'gross', 'screenplay-teleplay', 'LD spaciality', 'LD year') AND name.id = cast_info.person_id AND cast_info.movie_id = movie_info_idx.movie_id;