SELECT MIN(title.id), MIN(movie_info_idx.movie_id), MIN(movie_keyword.movie_id), MIN(title.production_year), MIN(complete_cast.movie_id), MIN(cast_info.movie_id) FROM title, cast_info, movie_info_idx, movie_keyword, complete_cast WHERE title.production_year >= 1893 AND title.id = cast_info.movie_id AND title.id = movie_keyword.movie_id AND title.id = complete_cast.movie_id AND movie_info_idx.movie_id = title.id;