SELECT MIN(company_name.name), MIN(movie_keyword.keyword_id), MIN(keyword.id), MIN(link_type.link), MIN(keyword.keyword), MIN(company_name.id), MIN(company_name.country_code), MIN(movie_companies.company_id) FROM movie_companies, link_type, movie_keyword, keyword, company_name WHERE keyword.keyword != 'electronic-bank-robbery' AND company_name.country_code IN ('[au]', '[ca]', '[bg]', '[kr]', '[th]', '[fr]', '[de]', '[ee]', '[it]', '[es]', '[gb]', '[ki]', '[bm]', '[na]', '[mn]', '[us]', '[in]', '[gl]', '[jp]', '[ve]') AND company_name.name != '20th Century Fox' AND link_type.link != 'remake of' AND keyword.id = movie_keyword.keyword_id AND company_name.id = movie_companies.company_id;