SELECT MIN(person_info.note), MIN(cast_info.person_id), MIN(movie_companies.note), MIN(comp_cast_type.kind), MIN(name.gender), MIN(name.id), MIN(link_type.link), MIN(movie_info.note), MIN(char_name.name) FROM movie_companies, link_type, movie_info, name, cast_info, char_name, person_info, comp_cast_type WHERE name.gender IN ('m', 'f', '') AND comp_cast_type.kind != 'complete+verified' AND movie_companies.note != '(supported by) (as Nordvisionsfonden)' AND movie_info.note IS NULL AND person_info.note IS NULL AND char_name.name IS NULL AND link_type.link != 'remade as' AND name.id = cast_info.person_id;